import React, { useState, useEffect } from "react";
import "./SpecificPlaces.css";

const SpecificPlaces = () => {
  const [travelData, setTravelData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const handleFormSubmit = async (event) => {
      const formData = event.detail;
      setLoading(true);
      setError(null);
      try {
        const response = await fetch("/SpecificPlaces", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(formData),
        });

        if (!response.ok) {
          throw new Error("Network response was not ok");
        }

        const data = await response.json();
        setTravelData(data);
        console.log("Success:", data);
      } catch (error) {
        console.error("Error:", error);
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    const container = document.querySelector(".specific-places-container");
    if (container) {
      container.addEventListener("formSubmit", handleFormSubmit);
    }

    return () => {
      if (container) {
        container.removeEventListener("formSubmit", handleFormSubmit);
      }
    };
  }, []);

  return (
    <div className="specific-places-container">
      {loading && <div className="loading">Loading...</div>}
      {error && <div className="error">Error: {error}</div>}
      {travelData && (
        <div className="travel-data">
          <h2>Travel Plan Details</h2>
          <div className="data-item">
            <strong>Current City:</strong> {travelData.data.currentCity}
          </div>
          <div className="data-item">
            <strong>Destination:</strong> {travelData.data.destination}
          </div>
          <div className="data-item">
            <strong>Days:</strong> {travelData.data.days}
          </div>
        </div>
      )}
    </div>
  );
};

export default SpecificPlaces;
