import React, { useState } from "react";
import "./EntryForm.css";
import SpecificPlaces from "./SpecificPlaces";

const EntryForm = () => {
  const [formData, setFormData] = useState({
    currentCity: "",
    destination: "",
    days: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Pass the form data to SpecificPlaces component
    const specificPlacesComponent = document.querySelector(
      ".specific-places-container"
    );
    if (specificPlacesComponent) {
      const event = new CustomEvent("formSubmit", { detail: formData });
      specificPlacesComponent.dispatchEvent(event);
    }
  };

  return (
    <div className="entry-form-container">
      <form onSubmit={handleSubmit} className="entry-form">
        <div className="form-group">
          <input
            type="text"
            id="currentCity"
            name="currentCity"
            value={formData.currentCity}
            onChange={handleChange}
            placeholder="Enter your current city"
            required
          />
        </div>

        <div className="form-group">
          <input
            type="text"
            id="destination"
            name="destination"
            value={formData.destination}
            onChange={handleChange}
            placeholder="Enter your destination"
            required
          />
        </div>

        <div className="form-group">
          <input
            type="number"
            id="days"
            name="days"
            value={formData.days}
            onChange={handleChange}
            placeholder="Enter number of days"
            min="1"
            required
          />
        </div>

        <button type="submit" className="submit-button">
          Go LaaaaaWaaaaaander! :)
        </button>
      </form>
      <SpecificPlaces />
    </div>
  );
};

export default EntryForm;
