import React, { useState, useEffect } from "react";
import { GoogleMap, useLoadScript, Marker } from "@react-google-maps/api";
import "./SpecificPlaces.css";

const mapContainerStyle = {
  width: "100%",
  height: "400px",
};

// List of 5 specific places (you can modify this)
const predefinedPlaces = [
  "The Republic of Užupis",
  "Gediminas' Tower",
  "Vilnius Old Town",
  // "Colosseum, Rome, Italy",
  // "Taj Mahal, Agra, India",
];

const SpecificPlacesMap = ({ travelData }) => {
  const [placeCoordinates, setPlaceCoordinates] = useState([]);
  const [center, setCenter] = useState({ lat: 0, lng: 0 });

  const { isLoaded, loadError } = useLoadScript({
    // googleMapsApiKey: "AIzaSyAlq7aoivZdmzARZ2dJlUg48EoBUF7cmQA",
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
    libraries: ["places"],
  });

  useEffect(() => {
    if (isLoaded) {
      const geocoder = new window.google.maps.Geocoder();
      let resultsCount = 0;
      const coordinates = [];

      predefinedPlaces.forEach((place, index) => {
        geocoder.geocode({ address: place }, (results, status) => {
          if (status === window.google.maps.GeocoderStatus.OK) {
            const location = results[0].geometry.location;
            coordinates.push({
              name: place,
              lat: location.lat(),
              lng: location.lng(),
            });

            resultsCount++;
            // When all places are geocoded
            if (resultsCount === predefinedPlaces.length) {
              setPlaceCoordinates(coordinates);
              // Set center to first place or calculate average
              setCenter({
                lat: coordinates[0].lat,
                lng: coordinates[0].lng,
              });
            }
          }
        });
      });
    }
  }, [isLoaded]);

  if (loadError) return <div>Error loading maps</div>;
  if (!isLoaded) return <div>Loading maps</div>;

  return (
    <div className="specific-places-container">
      <div className="travel-data">
        <h2>Travel Plan Details</h2>
        <div className="data-item">
          <strong>Current City:</strong> {travelData.data.currentCity}
        </div>
        <div className="data-item">
          <strong>Destination:</strong> {travelData.data.destination}
        </div>
        <div className="data-item">
          <strong>Days:</strong> {travelData.data.days}
        </div>
      </div>

      <GoogleMap
        mapContainerStyle={mapContainerStyle}
        zoom={2} // Lower zoom level to show multiple locations worldwide
        center={center}
      >
        {placeCoordinates.map((place, index) => (
          <Marker
            key={index}
            position={{ lat: place.lat, lng: place.lng }}
            label={place.name}
          />
        ))}
      </GoogleMap>

      <div style={{ marginTop: "20px" }}>
        <h2>Specific Places</h2>
        <ul>
          {placeCoordinates.map((place, index) => (
            <li key={index}>
              <strong>{place.name}</strong>
              <p>
                Lat: {place.lat.toFixed(4)}, Lng: {place.lng.toFixed(4)}
              </p>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default SpecificPlacesMap;
